mapper.select:
select * from t_user a,t_role_user b where a.id=b.user_id and b.role_id=#{role_id}
and lower(a.loginName) like lower('%${loginName}%')
and lower(a.name) like lower('%${name}%')

select count(*) from t_user a,t_role_user b where a.id=b.user_id and b.role_id=#{role_id}
and lower(a.loginName) like lower('%${loginName}%')
and lower(a.name) like lower('%${name}%')

select a.* from t_user a,t_position_org_user b where a.id=b.user_id and b.position_id=#{position_id}
and lower(a.loginName) like lower('%${loginName}%')
and lower(a.name) like lower('%${name}%')

select count(a.id) from t_user a,t_position_org_user b where a.id=b.user_id and b.position_id=#{position_id}
and lower(a.loginName) like lower('%${loginName}%')
and lower(a.name) like lower('%${name}%')
